pragma solidity >=0.4.21 <0.7.0;

contract KraczkowskyToken{
  string public name = "Kraczkowsky Token";
  string public symbol = "KRZ";
  string public standard = "Kraczkowsky Token v1.0";
  uint256 public totalSupply;
// Store the balance of each account that owns tokens using an address as a key and uint as value
  mapping(address => uint256) public balanceOf;
// See how much the account is allowed to spend using an address as key + second address as value that maps to a uint
  mapping(address => mapping(address => uint256)) public allowance;
// Emit event when a transfer is made
  event Transfer(
    address indexed _from,
    address indexed _to,
    uint256 _value
  );
// Emit event when a spend has been approved
  event Approval(
    address indexed owner,
    address indexed spender,
    uint256 _value
  );

  constructor (uint256 _initialSupply) public {
    balanceOf[msg.sender] = _initialSupply;
    totalSupply = _initialSupply;
  }
// implement a transfer function (per standard) to allow users to send tokens to another account
  function transfer(address _to, uint256 _numTokens) public returns (bool success){
    require(
      balanceOf[msg.sender] >= _numTokens,
      'ERROR (transfer): You do not have enough KRZ tokens to complete this transaction!'
    );

    balanceOf[msg.sender] -= _numTokens;
    balanceOf[_to] += _numTokens;

    emit Transfer(msg.sender, _to, _numTokens);

    return true;
  }
// implement an approve function (per standard) that allows another account to spend tokens, like on a cryptocurrency exchange.
function approve(address _delegate, uint256 _numTokens) public returns (bool success){
    allowance[msg.sender][_delegate] = _numTokens;

    emit Approval(msg.sender, _delegate, _numTokens);

    return true;
  }
// implement a transferFrom function (per standard) to establish a withdraw workflow, allowing contracts to transfer tokens on your behalf.
  function transferFrom(address _from, address _to, uint256 _value) public returns (bool success){
    require(
      _value <= balanceOf[_from],
      "REVERT: Balance of sending account has insufficient funds for this transfer!"
    );
    require(
      _value <= allowance[_from][msg.sender],
      "REVERT: You have not allowed this address to transfer this many tokens!"
    );

    balanceOf[_from] -= _value;
    balanceOf[_to] += _value;

    //allowance[_from];

  //  emit Transfer(_from, _to, _value);

    return true;
  }
}


contract KraczkowskyMarketplace {
// Consumers should be able to exchange
  KraczkowskyToken public tokenContract;

  mapping(string => address) public users;
  mapping(address => uint256) public gifts;

  function register (string memory _userName, address _userAddress) public {
    users[_userName] = _userAddress;
  }

  constructor (address _tokenContract) public {
    tokenContract = KraczkowskyToken(_tokenContract);
  }

  function addToken(address _tokenContract) public {
    tokenContract = KraczkowskyToken(_tokenContract);
  }

// Purchaser transfers gift amount to gift provider
  function purchaseGift (address _provider, uint256 _giftAmount) public {
    require(
      tokenContract.balanceOf(msg.sender) >= _giftAmount,
      'ERROR (purchaseGift): You do not have enough KRZ tokens to completes this transaction!'
    );

    gifts[_provider] = _giftAmount;

    tokenContract.transferFrom(msg.sender, _provider, _giftAmount);
  }

  function receivePayment () payable external {
    tokenContract.transfer(msg.sender, gifts[msg.sender]);
  }
}
